import React, { useState, useEffect } from 'react';
import './ExperienceCarousel.css'; // Make sure to import your CSS file

const experienceData = [
  {
    title: 'First Experience',
    subtitle: 'Subtitle 1',
    description: 'Description 1',
    image: 'image-url-1.jpg',
  },
  {
    title: 'Second Experience',
    subtitle: 'Subtitle 2',
    description: 'Description 2',
    image: 'image-url-2.jpg',
  },
  // Add more objects for the other experiences
];

const ExperienceCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isTextLeft, setIsTextLeft] = useState(true);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % experienceData.length);
      setIsTextLeft((prevIsTextLeft) => !prevIsTextLeft);
    }, 5000); // Change slide every 5 seconds

    return () => clearInterval(interval);
  }, []);

  const currentExperience = experienceData[currentIndex];

  return (
    <div className={`carousel-container ${isTextLeft ? 'text-left' : 'text-right'}`}>
      <div className="carousel-text">
        <h2>{currentExperience.title}</h2>
        <h3>{currentExperience.subtitle}</h3>
        <p>{currentExperience.description}</p>
      </div>
      <div className="carousel-image">
        <img src={currentExperience.image} alt={currentExperience.title} />
      </div>
    </div>
  );
};

export default ExperienceCarousel;
