import React, { useState} from 'react'
const Carousel = () => {
    const experienceData = [
        {
          title: 'First Experience',
          subtitle: 'Subtitle 1',
          description: 'Description 1',
          image: 'image-url-1.jpg',
        },
        {
          title: 'Second Experience',
          subtitle: 'Subtitle 2',
          description: 'Description 2',
          image: 'image-url-2.jpg',
        },
        {
          title: 'Third Experience',
          subtitle: 'Subtitle 3',
          description: 'Description 3',
          image: 'image-url-3.jpg',
        },
        {
          title: 'Fourth Experience',
          subtitle: 'Subtitle 4',
          description: 'Description 4',
          image: 'image-url-4.jpg',
        },
      ];
    const [currentIndex, setCurrentIndex] = useState(0);

    const goToIndex = (index) => {
      setCurrentIndex(index);
    };

    const goToNext = () => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % experienceData.length);
    };

    const goToPrevious = () => {
      setCurrentIndex((prevIndex) => (prevIndex - 1 + experienceData.length) % experienceData.length);
    };

    const currentItem = experienceData[currentIndex];

    return (
      <div className="carousel">
        <div className="carousel-content">
          <h2>{currentItem.title}</h2>
          <h3>{currentItem.subtitle}</h3>
          <p>{currentItem.description}</p>
          <img src={currentItem.image} alt={currentItem.title} />
        </div>
        <div className="carousel-buttons">
          {experienceData.map((_, index) => (
            <button
              key={index}
              className={index === currentIndex ? 'active' : ''}
              onClick={() => goToIndex(index)}
            >
              {index + 1}
            </button>
          ))}
        </div>
        <div className="carousel-navigation">
          <button onClick={goToPrevious}>Previous</button>
          <button onClick={goToNext}>Next</button>
        </div>
      </div>
    );
  };

  export default Carousel
