import React, { useState } from 'react';
import './Carousel.css'; // Import your CSS file for styling

const Carousel = () => {
  const experienceData = [
    {
      title: 'First Experience',
      subtitle: 'Subtitle 1',
      description: 'Description 1',
      image: 'image-url-1.jpg',
    },
    {
      title: 'Second Experience',
      subtitle: 'Subtitle 2',
      description: 'Description 2',
      image: 'image-url-2.jpg',
    },
    {
      title: 'Third Experience',
      subtitle: 'Subtitle 3',
      description: 'Description 3',
      image: 'image-url-3.jpg',
    },
    {
      title: 'Fourth Experience',
      subtitle: 'Subtitle 4',
      description: 'Description 4',
      image: 'image-url-4.jpg',
    },
  ];
  const goToNext = () => {
    const nextIndex = (currentIndex + 1) % experienceData.length;
    goToIndex(nextIndex);
  };

  const goToPrevious = () => {
    const prevIndex = (currentIndex - 1 + experienceData.length) % experienceData.length;
    goToIndex(prevIndex);
  };

  const [currentIndex, setCurrentIndex] = useState(0);
  const [hideToLeft, setHideToLeft] = useState(false);
  const [hideToRight, setHideToRight] = useState(false);

  const goToIndex = (index) => {
    if (index > currentIndex) {
      setHideToLeft(true);
      setTimeout(() => {
        setCurrentIndex(index);
        setHideToLeft(false);
        setHideToRight(true);
        setTimeout(() => {
          setCurrentIndex(index);
          setHideToRight(false);
        }, 500); // This should match the transition duration in CSS  
      }, 500); // This should match the transition duration in CSS
    } else if (index < currentIndex) {
      setHideToRight(true);
      setTimeout(() => {
        setCurrentIndex(index);
        setHideToRight(false);
        setHideToLeft(true);
        setTimeout(() => {
            setCurrentIndex(index);
            setHideToLeft(false);
          }, 500); 
      }, 500); // This should match the transition duration in CSS
    }
  };



  const currentItem = experienceData[currentIndex];

  return (
    <div className="carousel">
      <div
        className={`carousel-content ${
          hideToLeft ? 'hidden-left' : hideToRight ? 'hidden-right' : ''
        } ${currentIndex % 2 === 0 ? 'text-left' : 'text-right'}`}
      >
        <div className="text">
          <h2>{currentItem.title}</h2>
          <h3>{currentItem.subtitle}</h3>
          <p>{currentItem.description}</p>
        </div>
        <div className="image">
          <img src={currentItem.image} alt={currentItem.title} />
        </div>
      </div>
      <div className="carousel-buttons">
        {experienceData.map((_, index) => (
          <button
            key={index}
            className={index === currentIndex ? 'active' : ''}
            onClick={() => goToIndex(index)}
          >
            {index + 1}
          </button>
        ))}
      </div>
      <div className="carousel-navigation">
        <button onClick={goToPrevious}>Previous</button>
        <button onClick={goToNext}>Next</button>
      </div>
    </div>
  );
};

export default Carousel;
